$(document).ready(function(){const report=$(document).find(".password-strength-checker_report ul");const strengthMeter=$(document).find(".Password__strength-meter .Password__strength-meter--fill");$(document).on("change keydown keyup",".password-strength-checker_input_container input",function(){var score=calculateScore($(this).val());checkValidation($(this).val());if(score>80){strengthMeter.attr("data-score","4")}else if(score>60){strengthMeter.attr("data-score","3")}else if(score>40){strengthMeter.attr("data-score","2")}else if(score>25){strengthMeter.attr("data-score","1")}else if(score>0){strengthMeter.attr("data-score","0")}else{strengthMeter.removeAttr("data-score")}});function checkValidation(password){if(password.match(/\d+/)){report.find(".one-number").addClass("check").removeClass("cross")}else{report.find(".one-number").addClass("cross").removeClass("check")}if(password.match(/[a-z]/)){report.find(".lower-case").addClass("check").removeClass("cross")}else{report.find(".lower-case").addClass("cross").removeClass("check")}if(password.match(/[A-Z]/)){report.find(".upper-case").addClass("check").removeClass("cross")}else{report.find(".upper-case").addClass("cross").removeClass("check")}if(password.length>=8){report.find(".eight-character").addClass("check").removeClass("cross")}else{report.find(".eight-character").addClass("cross").removeClass("check")}if(password.match(/[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/)){report.find(".one-special-char").addClass("check").removeClass("cross")}else{report.find(".one-special-char").addClass("cross").removeClass("check")}}function calculateScore(password){var score=0;if(password.length<4){return-1}score+=password.length*4;score+=checkRepetition(1,password).length-password.length;score+=checkRepetition(2,password).length-password.length;score+=checkRepetition(3,password).length-password.length;score+=checkRepetition(4,password).length-password.length;if(password.match(/(.*[0-9].*[0-9].*[0-9])/)){score+=5}var symbols='.*[!,@,#,$,%,^,&,*,?,_,~]';symbols=new RegExp('('+symbols+symbols+')');if(password.match(symbols)){score+=5}if(password.match(/([a-z].*[A-Z])|([A-Z].*[a-z])/)){score+=10}if(password.match(/([a-zA-Z])/)&&password.match(/([0-9])/)){score+=15}if(password.match(/([!@#$%^&*?_~])/)&&password.match(/([0-9])/)){score+=15}if(password.match(/([!@#$%^&*?_~])/)&&password.match(/([a-zA-Z])/)){score+=15}if(password.match(/^\w+$/)||password.match(/^\d+$/)){score-=10}if(score>100){score=100}if(score<0){score=0}return score}function checkRepetition(length,str){var res="",repeated=!1;for(var i=0;i<str.length;i++){repeated=!0;for(var j=0;j<length&&(j+i+length)<str.length;j++){repeated=repeated&&(str.charAt(j+i)===str.charAt(j+i+length))}if(j<length){repeated=!1}if(repeated){i+=length-1;repeated=!1}else{res+=str.charAt(i)}}return res}})